//  WorkoutType.swift
//  Dropped
//
//  Defines the types of cycling workouts that can be generated by the AIWorkoutGenerator service.
//
//  - Used for user selection and AI prompt construction.
//  - Each case represents a distinct workout focus.
//
//  Edge Cases: None (enum is exhaustive and safe for UI use)
//
//  Limitations: Add new types here if expanding workout options.

import Foundation

/// Represents the type of cycling workout to generate.
/// - Conforms to: String (for raw value), CaseIterable (for UI), Identifiable (for SwiftUI lists)
enum WorkoutType: String, CaseIterable, Identifiable {
    case endurance
    case threshold
    case vo2Max
    case sprint
    case recovery

    /// Unique identifier for SwiftUI lists
    var id: String { rawValue }

    /// User-facing label for each workout type
    var displayName: String {
        switch self {
        case .endurance: return "Endurance"
        case .threshold: return "Threshold"
        case .vo2Max: return "VO2 Max"
        case .sprint: return "Sprint"
        case .recovery: return "Recovery"
        }
    }

    /// Brief description of each workout type
    var description: String {
        switch self {
        case .endurance: return "Long, steady efforts to build aerobic base."
        case .threshold: return "Sustained efforts near your FTP to improve power."
        case .vo2Max: return "High-intensity intervals to boost aerobic capacity."
        case .sprint: return "Short, maximal bursts to develop peak power."
        case .recovery: return "Easy riding to promote recovery."
        }
    }
}
